/**
 * 获取根路径
 * @return 返回根路径
 */
function getRootPath(){
  var strPath = window.location.pathname;
  var postPath = strPath.substring(0,strPath.substr(1).indexOf('/') + 1);
  return postPath;
}
/**
 * 绑定页面中所有form表单中:input元素的Enter键事件，实现焦点切换到下一个元素
 * @author 张进伟
 * @return
 */
function enterToTab(){
	$("form :input").keydown(function(){
		if (event.keyCode == 13) {
            return event.keyCode = 9;
        }
	});
}

/**
 * 初始化下拉多选框
 * @author CZX
 * @param comboxId
 * @param data
 * @param valueField
 * @param textField
 * @param callback
 * @returns
 */
function initMultiCombox(comboxId,data,valueField,textField,callback){
	$("#"+comboxId).combobox({
		data:data,
		valueField:valueField,
		textField:textField,
		formatter:function (row) {
            var opts = $(this).combobox('options');
            return '<input type="checkbox" class="combobox-checkbox">' + row[opts.textField];//关键在这一步，在项前面加一个checkbox。opts这个是combobox对象。

        },
        onLoadSuccess: function () {  //下拉框数据加载成功调用
    		//重新填写
            var opts = $(this).combobox('options');
            var target = this;
            var values = $(target).combobox('getValues');//获取选中的值的values
            $.map(values, function (value) {
                var el = opts.finder.getEl(target, value);
                el.find('input.combobox-checkbox')._propAttr('checked', true); 
            })
            if(null!=callback&&undefined!=callback&&typeof(eval(callback))=="function") {   
            	callback();   
            }   
        },
        onSelect: function (row) { //选中一个选项时调用
            var opts = $(this).combobox('options');
            //获取选中的值的values
            $(this).val($(this).combobox('getValues'));
           
		   //设置选中值所对应的复选框为选中状态
            var el = opts.finder.getEl(this, row[opts.valueField]);
            el.find('input.combobox-checkbox')._propAttr('checked', true);
        },
        onUnselect: function (row) {//不选中一个选项时调用
            var opts = $(this).combobox('options');
            //获取选中的值的values
            $(this).val($(this).combobox('getValues'));
          
            var el = opts.finder.getEl(this, row[opts.valueField]);
            el.find('input.combobox-checkbox')._propAttr('checked', false);
        }
	});	
}

function checkLogin(data){
	if ((data+"").indexOf("超时")>-1){
		alert("页面超时，请重新登录");
		top.location.href = getRootPath();
	}
}

/**
 * 绑定页面中所有form表单中:input元素(不包括button)的Enter键事件，实现焦点切换到下一个元素
 * @return
 */
function enterToTabExcButton(){
	$("form :input[type!='button']").keydown(function(){
		if (event.keyCode == 13) {
            return event.keyCode = 9;
        }
	});
}

/**
 * 加上form表单中:input元素的disabled属性
 * @author 张进伟
 * @param formId 表单id
 * @return
 */
function disabledFormInput(formId){
	$("#" + formId + " :input").each(function(i) {
		$(this).attr("disabled","disabled");
	});
}

/**
 * 加上form表单中:input元素(除了type='checkbox'类型)的disabled属性
 * @author 张进伟
 * @param formId 表单id
 * @return
 */
function disFormInputExceptCheckbox(formId){
	$("#" + formId + " :input[type!='checkbox']||:input[type!='button']").each(function(i) {
		$(this).attr("disabled","disabled");
	});
}
/**
 * 去掉form表单中:input元素的disabled属性
 * @author 张进伟
 * @param formId 表单id
 * @return
 */
function enableFormInput(formId){
	$("#" + formId + " :input").each(function(i) {
		$(this).removeAttr("disabled");
	});
}

/**
 * 加上form表单中:checkbox元素的checked属性
 * @author 张进伟
 * @param formId
 * @return
 */
function addFormChecked(formId){
	$("#" + formId + " :checkbox").each(function(i) {
		$(this).attr("checked","checked");
	});
}

/**
 * 去掉form表单中:checkbox元素的checked属性
 * @author 张进伟
 * @param formId
 * @return
 */
function removeFormChecked(formId){
	$("#" + formId + " :checkbox").each(function(i) {
		$(this).removeAttr("checked");
	});
}

/**
 * 验证某一字符串是否含有非法字符
 * @author 张进伟
 * @param regExp 正则表达式
 * @param str 字符串
 * @return
 */
function isIllegalRegExp(regExp,str){
	if(str != "" ){
		return (!(regExp.test(str)));
	}else {
		return false;
	}
}
/**
 * 去掉form表单提交的数据的空格
 * @author 张进伟
 * @param formId 表单id
 * @return
 */
function trimForm(formId){
	var textNodes = $("#"+formId+" :input[type='text']");
	textNodes.each(function(i){
		$(this).val($.trim($(this).val()));
	});
}
/**
 * 获得所有结果
 * @param str 输入的字符串
 * @return 首字母的字符串数组
 */
function letterArr(str) {
	var res = "";
	for ( var i = 0; i < str.length; i++) { // 输入的字符串的每一个字符，作为参数调用checkCh()函数
		res += checkCh(str.charAt(i));
	}
	return res;
}
/**
 * 根据输入返回不同值
 * @param ch 输入的字符串中的一个字符
 * @return 不是汉字(unicode 19968-40869 为汉字)，返回原字符
 * @return 是汉字且为多音字，返回汉字对应的首字母字符串
 * @return 是汉字且不为多音字，调用getCh函数，并返回汉字对应的首字母字符
 */
function checkCh(ch) {
	var uni = ch.charCodeAt(0);
	if (uni > 40869 || uni < 19968){
		// 不是汉字,返回原字符
		return ch;
	}else{
		return getCh(ch);
	}
}
/**
 * 根据汉字所属数组的id获取该汉字的首字母
 * @param str 输入的字符串中的一个字符
 * @return 字符对应的首字母
 */
function getCh(ch) {
	var letter = "";
	for ( var i = 0; i < arrInitialChart.length; i++) {
		if (arrInitialChart[i].indexOf(ch) >= 0) { // 当str在arrInitialChart[i]数组中
			letter = arrNumLetter[i]; // letter被赋值arrNumLetter数组对应的字符首字母
		}
	}
	return letter;
}
