/**
 * 验证用户是否登录
 * @author 孙宏旭
 * @param path 根路径
 * @return
 */
function isLogin(path){
	var loginFlag = new Boolean(false);
	$.ajax({
		   type: "POST",
		   async: false,  //设置为同步发送请求
		   url:  path + "/login/loginAdmit!isLoginUser.do?serialId="+new Date().toString(),
		   success: function(data){
		   	var obj = eval(data);
			var str=obj["mess"];
		     if (str== "notLogin"){
			     alert("您未登录或登录已超时，请重新登录！");
			     window.top.location.href = path+"/login/loginAdmit!logoutUser.do?serialId=" + new Date().toString();
			     loginFlag = false;
		     }else{
			     loginFlag = true;
		     }
		   }
		});
	return loginFlag;
}
