function pagPages(totalCount,page,perPage,name){
    var totalPage=totalCount%perPage==0?parseInt(totalCount/perPage):(parseInt(totalCount/perPage)+1);   //获取总页数	
    if(totalPage==0)
    	totalPage=1;
    if(page==-1)
    	page=totalPage;
    if(name==undefined)
    	name=".pagination-table";
    $(name).find(".totalPage").remove();
    //$totalElement='<label class="totalPage" style="margin-right: 5px;">共<span style="font-weight:700">'+totalPage+'</span>页</label>';
   // $(name).find("label:contains(当前页)");
	var firstPage=$(name+" #firstPage");
	var previousPage=$(name+" #previousPage");
	var nextPage=$(name+" #nextPage");
	var lastPage=$(name+" #lastPage");
	var pageSelect=$(name+" #pageSelect ul");
	if(totalPage==1){
    	firstPage.attr("disabled","disabled");
		previousPage.attr("disabled","disabled");
		nextPage.attr("disabled","disabled");	
		lastPage.attr("disabled","disabled");	
	}else{
		if(page==1){
	    	firstPage.attr("disabled","disabled");	
			previousPage.attr("disabled","disabled");	
			nextPage.removeAttr("disabled");		
			lastPage.removeAttr("disabled");				
		}else if(page<totalPage){
	    	firstPage.removeAttr("disabled");
			previousPage.removeAttr("disabled");
			nextPage.removeAttr("disabled");		
			lastPage.removeAttr("disabled");						
		}else{
	    	firstPage.removeAttr("disabled");
			previousPage.removeAttr("disabled");
			nextPage.attr("disabled","disabled");		
			lastPage.attr("disabled","disabled");	
		}
	}
	pageSelect.empty();
	for(var i=0;i<totalPage;i++){
		pageSelect.append($("<li><a data-option='"+(i+1)+"' tabindex='-1'>"+(i+1)+"</a></li>"));
	}
}
