Date.prototype.format =function(format)
{
    var o = {
    "M+" : this.getMonth()+1, //month
	"d+" : this.getDate(),    //day
	"h+" : this.getHours(),   //hour
	"m+" : this.getMinutes(), //minute
	"s+" : this.getSeconds(), //second
	"q+" : Math.floor((this.getMonth()+3)/3),  //quarter
	"S" : this.getMilliseconds() //millisecond
    }
    if(/(y+)/.test(format)) format=format.replace(RegExp.$1,
    (this.getFullYear()+"").substr(4- RegExp.$1.length));
    for(var k in o)if(new RegExp("("+ k +")").test(format))
    format = format.replace(RegExp.$1,
    RegExp.$1.length==1? o[k] :
    ("00"+ o[k]).substr((""+ o[k]).length));
    return format;
}

//清空查询列表
function clearForm(form)  
{  
    var formObj = document.getElementById(form);  
    if(formObj == undefined)  
    {  
        return;  
    }  
      
    for(var i=0; i<formObj.elements.length; i++)  
    {  
        if(formObj.elements[i].type == "text")  
        {  
            formObj.elements[i].value = "";  
        }  
        else if(formObj.elements[i].type == "password")  
        {  
            formObj.elements[i].value = "";  
        }  
        else if(formObj.elements[i].type == "radio")  
        {  
            formObj.elements[i].checked = false;  
        }  
        else if(formObj.elements[i].type == "checkbox")  
        {  
            formObj.elements[i].checked = false;  
        }  
        else if(formObj.elements[i].type == "select-one")  
        {  
            formObj.elements[i].options[0].selected = true;  
        }  
        else if(formObj.elements[i].type == "select-multiple")  
        {     
            for(var j = 0; j < formObj.elements[i].options.length; j++)  
            {  
                formObj.elements[i].options[j].selected = false;  
            }  
        }  
        else if(formObj.elements[i].type == "file")  
        {  
            //formObj.elements[i].select();  
            //document.selection.clear();             
            // for IE, Opera, Safari, Chrome  
            var file = formObj.elements[i];  
             if (file.outerHTML) {  
                 file.outerHTML = file.outerHTML;  
             } else {  
                 file.value = "";  // FF(包括3.5)  
            }  
        }  
        else if(formObj.elements[i].type == "textarea")  
        {  
            formObj.elements[i].value = "";  
        }  
    }  
}
//对传参进行加密
function decodeURL(url){
	if(url!=null&&url!=""){
		//进行编码  
	   url=url.replace("_*colon*_",":" )  
	   //.replace("_*slash*_","/")  
	   .replace( "_*backslash*_","\\")  
	   .replace( "_*question*_","?")  
	   .replace("_*equal*_","=" )
	   .replace("_*comma*_", ",")
	   .replace("_*semicolon*_",";")
	   .replace("_*minus*_","-")
	}
	   return url;  
}
//对返回的值进行解码
function encodeURL(url){
   if(url!=null&&url!=""){
   //修正反斜杠为斜杠  
   url=url.replace("\\", "/");  
   //使用长文本代替要保留字符串  
   url=url.replace(":", "_*colon*_")  
   //.replace("/", "_*slash*_")  
   .replace("\\", "_*backslash*_")  
   .replace("?", "_*question*_")  
   .replace("=", "_*equal*_")
   .replace(",", "_*comma*_")
   .replace(";","_*semicolon*_")
   .replace("-","_*minus*_")
   .replace(/\+/g, '%2B');
   }
   return url;
}

